import { AutoSizer, Props, Size } from "./AutoSizer";

export default AutoSizer;

export type { Props, Size };
import {
  Component,
  createElement,
  CSSProperties,
  HTMLAttributes,
  ReactElement,
} from "react";

// @ts-ignore
import { createDetectElementResize } from "../vendor/detectElementResize";

export type Size = {
  // Legacy width and height parameters (offsetWidth and offsetHeight)
  height?: number;
  width?: number;

  // Take transform:scale into account (getBoundingClientRect)
  scaledHeight?: number;
  scaledWidth?: number;
};

export type Props = {
  children: (size: Size) => ReactElement;
  defaultHeight?: number;
  defaultWidth?: number;
  disableHeight?: boolean;
  disableWidth?: boolean;
  nonce?: string;
  onResize?: (size: Size) => void;
  tagName?: string;
