  static defaultProps = {
    onResize: () => {},
    disableHeight: false,
    disableWidth: false,
    style: {},
  };

  state = {
    height: this.props.defaultHeight || 0,
    scaledHeight: this.props.defaultHeight || 0,
    scaledWidth: this.props.defaultWidth || 0,
    width: this.props.defaultWidth || 0,
