import React, { useEffect, useRef, useState } from 'react';

export const useDebounce = <T>(
    value: T,
    delay: number
): [T, React.Dispatch<React.SetStateAction<T>>] => {
    const [debouncedValue, setDebouncedValue] = useState(value);

    useEffect(() => {
        const handler = setTimeout(() => {
            setDebouncedValue(value);
        }, delay);

        return () => {
            clearTimeout(handler);
        };
    }, [value, delay]);

    return [debouncedValue, setDebouncedValue];
};

const UNINITIALIZED_SENTINEL = {};
export const useStaticValue = <T>(factory: () => T): T => {
    const valueRef = useRef<T>(UNINITIALIZED_SENTINEL as T);
    if (valueRef.current === UNINITIALIZED_SENTINEL) valueRef.current = factory();
    return valueRef.current;
};

export const useInstanceVariable = <T>(value: T) => {
    const ref = useRef(value);
    useEffect(() => {
        ref.current = value;
    }, [ref, value]);
    return ref;
};
export class Logger {
    public static error(...args: any[]): void {
        // eslint-disable-next-line no-console
        console.error('[Chonky runtime error]', ...args);
    }

    public static warn(...args: any[]): void {
        // eslint-disable-next-line no-console
        console.warn('[Chonky runtime warning]', ...args);
    }

    public static debug(...args: any[]): void {
        // eslint-disable-next-line no-console
        console.debug('[Chonky runtime debug]', ...args);
    }

    public static formatBullets(bullets: string[]): string {
        return `\n- ${bullets.join('\n- ')}`;
    }
}
import { Nullable } from 'tsdef';

import { FileData } from '../types/file.types';
import { Logger } from './logger';

export class FileHelper {
    public static isDirectory(file: Nullable<FileData>): file is FileData {
        // Not a directory by default
        return !!file && file.isDir === true;
    }

    public static isHidden(file: Nullable<FileData>): file is FileData {
        // Not hidden by default
        return !!file && file.isHidden === true;
    }

    public static isSymlink(file: Nullable<FileData>): file is FileData {
        // Not a symlink by default
        return !!file && file.isSymlink === true;
    }

    public static isEncrypted(file: Nullable<FileData>): file is FileData {
        // Not encrypted by default
        return !!file && file.isEncrypted === true;
    }

    public static isClickable(file: Nullable<FileData>): file is FileData {
        // Clickable by default
        return !!file;
    }

    public static isOpenable(file: Nullable<FileData>): file is FileData {
        // Openable by default
        return !!file && file.openable !== false;
    }

    public static isSelectable(file: Nullable<FileData>): file is FileData {
        // Selectable by default
        return !!file && file.selectable !== false;
    }

    public static isDraggable(file: Nullable<FileData>): file is FileData {
        // File & folders are draggable by default, `null` is not
        return !!file && file.draggable !== false;
    }

    public static isDroppable(file: Nullable<FileData>): file is FileData {
        // Folders are droppable by default, files are not
        if (!file) return false;
        if (file.isDir && file.droppable !== false) return true;
        return file.droppable === true;
    }

    public static isDndOpenable(file: Nullable<FileData>): file is FileData {
        // Folders are DnD openable by default, files are not
        if (!FileHelper.isOpenable(file)) return false;
        if (file.isDir && file.dndOpenable !== false) return true;
        return file.dndOpenable === true;
    }

    public static getModDate(file: Nullable<FileData>): Nullable<Date> {
        if (!file || file.modDate === null || file.modDate === undefined) return null;
        return FileHelper.parseDate(file.modDate);
    }

    public static parseDate(maybeDate: Date | string | any): Nullable<Date> {
        if (typeof maybeDate === 'string' || typeof maybeDate === 'number') {
            // We allow users to provide string and numerical representations of dates.
            try {
                return new Date(maybeDate);
            } catch (error) {
                Logger.error(
                    `Could not convert provided string/number into a date: ${error.message} `,
                    'Invalid value:',
                    maybeDate
                );
            }
        }
        if (maybeDate instanceof Date && !isNaN(maybeDate.getTime())) {
            // We only allow valid dates objects
            return maybeDate;
        }

        // If we have an invalid date representation, we just return null.
        Logger.warn('Unsupported date representation:', maybeDate);
        return null;
    }

    public static getChildrenCount(file: Nullable<FileData>): Nullable<number> {
        if (!file || typeof file.childrenCount !== 'number') return null;

        return file.childrenCount;
    }
}
import { Nullable } from 'tsdef';

import { FileAction } from '../types/action.types';
import { FileArray } from '../types/file.types';
import { Logger } from '../util/logger';

interface SanitizeFiles {
    (mode: 'files', rawArray: FileArray | any): {
        sanitizedArray: FileArray;
        errorMessages: string[];
    };
    (mode: 'folderChain', rawArray: Nullable<FileArray> | any): {
        sanitizedArray: FileArray;
        errorMessages: string[];
    };
    (mode: 'fileActions', rawArray: FileAction[] | any): {
        sanitizedArray: FileAction[];
        errorMessages: string[];
    };
}

export const sanitizeInputArray: SanitizeFiles = (mode: string, rawArray: any[]) => {
    const sanitizedFiles = [];
    const errorMessages: string[] = [];

    if ((mode === 'folderChain' || mode === 'fileActions') && !rawArray) {
        // Do nothing, we allow folder chain to be null.
    } else if (!Array.isArray(rawArray)) {
        errorMessages.push(
            `Expected "${mode}" prop to be an array, got "${typeof rawArray}" instead.`
        );
    } else {
        let nonObjectFileCount = 0;
        let missingFieldFileCount = 0;
        const seenIds = new Set<string>();
        const duplicateIds = new Set<string>();

        for (let i = 0; i < rawArray.length; ++i) {
            const item = rawArray[i];
            if (!item) {
                if (mode === 'fileActions') nonObjectFileCount++;
                else sanitizedFiles.push(null);
            } else if (typeof item !== 'object') {
                nonObjectFileCount++;
            } else {
                if (!item.id || (mode !== 'fileActions' && !item.name)) {
                    missingFieldFileCount++;
                } else if (seenIds.has(item.id)) {
                    duplicateIds.add(item.id);
                } else {
                    seenIds.add(item.id);
                    sanitizedFiles.push(item);
                }
            }
        }

        if (nonObjectFileCount) {
            errorMessages.push(
                `Detected ${nonObjectFileCount} file(s) of invalid type. Remember ` +
                    `that "files" array should contain either objects or nulls.`
            );
        }
        if (missingFieldFileCount) {
            errorMessages.push(
                `Detected ${missingFieldFileCount} file(s) that are missing the ` +
                    `required fields. Remember that file object should define an ` +
                    `"id" and a "name".`
            );
        }
        if (duplicateIds.size > 0) {
            const repeatedIdsString = '"' + Array.from(duplicateIds).join('", "') + '"';
            errorMessages.push(
                `Detected ${duplicateIds.size} file IDs that are used multiple ` +
                    `times. Remember that each file should have a unique IDs. The ` +
                    `following IDs were seen multiple times: ${repeatedIdsString}`
            );
        }
    }

    if (errorMessages.length > 0) {
        const errorMessageString = '\n- ' + errorMessages.join('\n- ');
        let arrayString: string;
        let itemString: string;
        if (mode === 'folderChain') {
            arrayString = 'folder chain';
            itemString = 'files';
        } else if (mode === 'fileActions') {
            arrayString = 'file actions';
            itemString = 'file actions';
        } else {
            // mode === 'files'
            arrayString = 'files';
            itemString = 'files';
        }

        Logger.error(
            `Errors were detected when sanitizing the ${arrayString} array. ` +
                `Offending ${itemString} were removed from the array. Summary of ` +
                `validation errors: ${errorMessageString}`
        );
    }

    return {
        sanitizedArray: sanitizedFiles,
        errorMessages,
    };
};
/**
 * We have option IDs in a separate file to avoid circular deps...
 */
export const OptionIds = {
    ShowHiddenFiles: 'show_hidden_files',
    ShowFoldersFirst: 'show_folders_first',
    DarkMode: 'dark_mode',
};
import { Nullable } from 'tsdef';

import { FileData } from './file.types';

export type FileSortKeySelector = (file: Nullable<FileData>) => any;

export enum SortOrder {
