export const reduxThunks = {
    selectRange: (params: {
        rangeStart: number;
        rangeEnd: number;
        reset?: boolean;
    }): ChonkyThunk => (dispatch, getState) => {
        const state = getState();
        if (state.disableSelection) return;
        const displayFileIds = selectors.getDisplayFileIds(state);
        const fileIdsToSelect = displayFileIds
            .slice(params.rangeStart, params.rangeEnd + 1)
            .filter(id => id && FileHelper.isSelectable(state.fileMap[id])) as string[];
        dispatch(
            reduxActions.selectFiles({
                fileIds: fileIdsToSelect,
                reset: !!params.reset,
            })
        );
    },
};
import { reduxActions } from '../redux/reducers';
import {
    getFileData, getIsFileSelected, selectDisableSelection, selectors, selectParentFolder,
    selectSelectionSize
} from '../redux/selectors';
import { reduxThunks } from '../redux/thunks';
import { thunkRequestFileAction } from '../redux/thunks/dispatchers.thunks';
import {
    ChangeSelectionPayload, EndDragNDropPayload, KeyboardClickFilePayload, MouseClickFilePayload,
    MoveFilesPayload, OpenFileContextMenuPayload, OpenFilesPayload, StartDragNDropPayload
} from '../types/action-payloads.types';
import { ChonkyIconName } from '../types/icons.types';
import { FileHelper } from '../util/file-helper';
